/**
 * useAnimatePresence Hook
 * 
 * Replacement for Framer Motion's AnimatePresence.
 * Handles enter/exit animations for conditional rendering.
 */

import React, { useState, useEffect, useRef } from 'react';
import { useFlowMotion } from './useFlowMotion';

export interface AnimatePresenceConfig {
  initial?: boolean;
  exitBeforeEnter?: boolean;
  mode?: 'wait' | 'sync';
}

export interface UseAnimatePresenceOptions {
  isVisible: boolean;
  initial?: { opacity?: number; scale?: number; x?: number; y?: number };
  animate?: { opacity?: number; scale?: number; x?: number; y?: number };
  exit?: { opacity?: number; scale?: number; x?: number; y?: number };
  transition?: { duration?: number; easing?: 'ease' | 'ease-in' | 'ease-out' | 'ease-in-out' | 'linear' };
  onExitComplete?: () => void;
}

/**
 * Hook for handling enter/exit animations
 * Replaces AnimatePresence + motion component pattern
 */
export function useAnimatePresence({
  isVisible,
  initial = { opacity: 0 },
  animate = { opacity: 1 },
  exit = { opacity: 0 },
  transition = { duration: 300, easing: 'ease-out' },
  onExitComplete,
}: UseAnimatePresenceOptions) {
  const [shouldRender, setShouldRender] = useState(isVisible);
  const [isExiting, setIsExiting] = useState(false);
  const timeoutRef = useRef<number | null>(null);

  useEffect(() => {
    if (isVisible) {
      setShouldRender(true);
      setIsExiting(false);
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    } else {
      setIsExiting(true);
      const duration = transition.duration || 300;
      timeoutRef.current = window.setTimeout(() => {
        setShouldRender(false);
        setIsExiting(false);
        onExitComplete?.();
      }, duration);
    }

    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, [isVisible, transition.duration, onExitComplete]);

  // Determine which animation state to use
  const targetState = isExiting ? exit : (isVisible ? animate : initial);

  // Normalize state to ensure all properties are defined
  const normalizedState = {
    opacity: targetState.opacity ?? 1,
    scale: targetState.scale ?? 1,
    x: targetState.x ?? 0,
    y: targetState.y ?? 0,
  };

  // Use useFlowMotion for smooth animation
  const animatedStyle = useFlowMotion(
    normalizedState,
    {
      duration: transition.duration || 300,
      easing: transition.easing || 'ease-out',
    }
  );

  return {
    shouldRender,
    style: {
      opacity: animatedStyle.opacity,
      transform: [
        animatedStyle.scale !== 1 && `scale(${animatedStyle.scale})`,
        animatedStyle.x !== 0 && `translateX(${animatedStyle.x}px)`,
        animatedStyle.y !== 0 && `translateY(${animatedStyle.y}px)`,
      ].filter(Boolean).join(' ') || undefined,
    },
  };
}

/**
 * AnimatePresence Component
 * 
 * Drop-in replacement for Framer Motion's AnimatePresence
 */
export interface AnimatePresenceProps {
  children: React.ReactNode;
  mode?: 'wait' | 'sync';
  initial?: boolean;
}

export const AnimatePresence: React.FC<AnimatePresenceProps> = ({
  children,
  mode = 'sync',
  initial = true,
}) => {
  // For sync mode, just render children
  // Individual components handle their own animations via useAnimatePresence
  return <React.Fragment>{children}</React.Fragment>;
};

/**
 * ALS Context
 * 
 * ALS is passive - it ONLY displays what Prime Brain tells it.
 * This keeps ALS as the visual heartbeat of the Studio.
 */

import React, { createContext, useContext, useState, useCallback } from 'react';
import type { BehaviorState } from './behaviorEngine';

interface ALSState {
  flow: number;
  pulse: number;
  tension: number;
  momentum: number;
  hushFlags: string[];
}

interface ALSContextValue {
  state: ALSState;
  setState: (state: Partial<ALSState>) => void;
}

const ALSContext = createContext<ALSContextValue | null>(null);

export function useALS() {
  const context = useContext(ALSContext);
  if (!context) {
    throw new Error('useALS must be used within ALSProvider');
  }
  return context;
}

interface ALSProviderProps {
  children: React.ReactNode;
}

export function ALSProvider({ children }: ALSProviderProps) {
  const [state, setStateInternal] = useState<ALSState>({
    flow: 0,
    pulse: 0,
    tension: 0,
    momentum: 0,
    hushFlags: [],
  });

  // Use ref to track if we're in a batch update to reduce re-renders
  const updateTimeoutRef = React.useRef<number | null>(null);

  const setState = useCallback((updates: Partial<ALSState>) => {
    // Batch updates to reduce re-renders from flow loop
    if (updateTimeoutRef.current !== null) {
      clearTimeout(updateTimeoutRef.current);
    }

    updateTimeoutRef.current = window.setTimeout(() => {
      setStateInternal(prev => {
        // Only update if values actually changed (prevent unnecessary re-renders)
        const hasChanges = Object.keys(updates).some(key => {
          const typedKey = key as keyof ALSState;
          if (typedKey === 'hushFlags') {
            const prevFlags = prev.hushFlags || [];
            const newFlags = updates.hushFlags || [];
            return JSON.stringify(prevFlags) !== JSON.stringify(newFlags);
          }
          return Math.abs((prev[typedKey] as number) - (updates[typedKey] as number)) > 0.01;
        });

        return hasChanges ? { ...prev, ...updates } : prev;
      });
      updateTimeoutRef.current = null;
    }, 16); // ~60fps update rate instead of every 40ms
  }, []);

  React.useEffect(() => {
    return () => {
      if (updateTimeoutRef.current !== null) {
        clearTimeout(updateTimeoutRef.current);
      }
    };
  }, []);

  return (
    <ALSContext.Provider value={{ state, setState }}>
      {children}
    </ALSContext.Provider>
  );
}

/**
 * Bloom Context
 * 
 * Bloom is ALWAYS hidden unless summoned.
 * But Bloom LISTENS and gets preloaded with context when Prime Brain
 * detects Flow, Creative Burst, Precision Editing, or Performance Mode.
 * 
 * Bloom doesn't open. Bloom ANTICIPATES.
 */

import React, { createContext, useContext, useState, useCallback } from 'react';
import type { BehaviorState, FlowMode } from './behaviorEngine';

interface BloomPreparedContext {
  mode: FlowMode;
  commonActions: string[];
  predictions: string[];
  flow: number;
  pulse: number;
  tension: number;
}

interface BloomContextValue {
  preparedContext: BloomPreparedContext | null;
  prepare: (context: Partial<BloomPreparedContext>) => void;
  isVisible: boolean;
  setIsVisible: (visible: boolean) => void;
}

const BloomContext = createContext<BloomContextValue | null>(null);

export function useBloom() {
  const context = useContext(BloomContext);
  if (!context) {
    throw new Error('useBloom must be used within BloomProvider');
  }
  return context;
}

interface BloomProviderProps {
  children: React.ReactNode;
}

export function BloomProvider({ children }: BloomProviderProps) {
  const [preparedContext, setPreparedContext] = useState<BloomPreparedContext | null>(null);
  const [isVisible, setIsVisible] = useState(false);

  const prepare = useCallback((context: Partial<BloomPreparedContext>) => {
    setPreparedContext(prev => ({
      mode: context.mode ?? prev?.mode ?? 'idle',
      commonActions: context.commonActions ?? prev?.commonActions ?? [],
      predictions: context.predictions ?? prev?.predictions ?? [],
      flow: context.flow ?? prev?.flow ?? 0,
      pulse: context.pulse ?? prev?.pulse ?? 0,
      tension: context.tension ?? prev?.tension ?? 0,
    }));
  }, []);

  return (
    <BloomContext.Provider value={{ preparedContext, prepare, isVisible, setIsVisible }}>
      {children}
    </BloomContext.Provider>
  );
}

/**
 * Prime Brain Context
 * 
 * Provides Prime Brain state and behavior computation to the flow loop.
 * Integrates with existing Prime Brain orchestration.
 */

import React, { createContext, useContext, useState, useCallback, useRef, useEffect } from 'react';
import type { SessionSignals } from './gatherSessionSignals';
import { computeBehavior, type BehaviorState, type FlowMode } from './behaviorEngine';
import { scheduleAITask } from '../quantum';
import type { PrimeBrainStatus } from '../../types/primeBrainStatus';
import { als } from '../../utils/alsFeedback';

interface PrimeBrainContextValue {
  state: BehaviorState;
  primeBrainStatus: PrimeBrainStatus | null;
  updateFromSession: (signals: SessionSignals) => void;
  updateFromALS: (alsState: { flow: number; pulse: number; tension: number }) => void;
}

const PrimeBrainContext = createContext<PrimeBrainContextValue | null>(null);

export function usePrimeBrain() {
  const context = useContext(PrimeBrainContext);
  if (!context) {
    throw new Error('usePrimeBrain must be used within PrimeBrainProvider');
  }
  return context;
}

interface PrimeBrainProviderProps {
  children: React.ReactNode;
  primeBrainStatus: PrimeBrainStatus | null;
}

export function PrimeBrainProvider({ children, primeBrainStatus }: PrimeBrainProviderProps) {
  const [state, setState] = useState<BehaviorState>({
    flow: 0,
    pulse: 0,
    momentum: 0,
    tension: 0,
    hushWarnings: [],
    mode: 'idle',
  });

  const lastSignalsRef = useRef<SessionSignals | null>(null);

  const updateFromSession = useCallback((signals: SessionSignals) => {
    lastSignalsRef.current = signals;

    // Schedule behavior computation as AI task (can defer if needed)
    scheduleAITask(
      `prime-brain-behavior-${Date.now()}`,
      () => {
        const behavior = computeBehavior(signals);
        setState(behavior);
      },
      30, // 30ms budget for behavior computation
      (actualMs, budgetMs) => {
        // Only log overruns in dev mode and if significantly over budget (2x)
        if (import.meta.env.DEV && actualMs > budgetMs * 2) {
          als.warning(`[Prime Brain] Behavior computation overrun: ${actualMs.toFixed(2)}ms (budget: ${budgetMs}ms)`);
        }
      }
    );
  }, []);

  const updateFromALS = useCallback((alsState: { flow: number; pulse: number; tension: number }) => {
    // Feedback loop: ALS state influences Prime Brain
    // This creates the continuous rhythm
    // For now, we just update state based on ALS feedback
    // The behavior engine will incorporate this in the next tick
    setState(prev => ({
      ...prev,
      flow: Math.max(prev.flow, alsState.flow * 0.3), // Subtle influence
      pulse: Math.max(prev.pulse, alsState.pulse * 0.3),
      tension: Math.max(prev.tension, alsState.tension * 0.3),
    }));
  }, []);

  return (
    <PrimeBrainContext.Provider value={{ state, primeBrainStatus, updateFromSession, updateFromALS }}>
      {children}
    </PrimeBrainContext.Provider>
  );
}

/**
 * Session Core Context
 * 
 * Session Core is the DAW's internal engine:
 * - audio graph
 * - routing
 * - clip ops
 * - timeline logic
 * - piano roll
 * - drum grid
 * - transport state
 * 
 * Session Core modifies behavior based on Prime Brain signals.
 */

import React, { createContext, useContext, useState, useCallback, useRef } from 'react';
import type { BehaviorState, FlowMode } from './behaviorEngine';

interface SessionCoreState {
  snapPrecision: number; // Increased in precision mode
  scrollSmoothing: number; // Enabled in flow mode
  prefetchEnabled: boolean; // Enabled in burst mode
  lowLatencyMode: boolean; // Enabled in record/performance mode
  uiAnimationReduced: boolean; // Reduced animations in performance mode
  bloomSuppressed: boolean; // Suppress Bloom in performance mode
  // Punch Mode settings
  punchMode: boolean; // Punch Mode active
  autoCrossfade: boolean; // Auto-crossfade enabled in punch mode
  preRollAuto: boolean; // Auto pre-roll in punch mode
  postRollAuto: boolean; // Auto post-roll trim in punch mode
  waveformSmoothing: boolean; // Waveform smoothing in punch mode
  snapDisabled: boolean; // Snap disabled in punch mode
  // Auto-Punch settings
  autoPunchRegion?: { start: number; end: number }; // Predicted punch region
}

interface SessionCoreContextValue {
  state: SessionCoreState;
  applyBrainState: (brainState: BehaviorState) => void;
  preparePunchRegion?: (start: number, end: number) => void;
}

const SessionCoreContext = createContext<SessionCoreContextValue | null>(null);

export function useSessionCore() {
  const context = useContext(SessionCoreContext);
  if (!context) {
    throw new Error('useSessionCore must be used within SessionCoreProvider');
  }
  return context;
}

interface SessionCoreProviderProps {
  children: React.ReactNode;
}

export function SessionCoreProvider({ children }: SessionCoreProviderProps) {
  const [state, setState] = useState<SessionCoreState>({
    snapPrecision: 1.0,
    scrollSmoothing: 0.5,
    prefetchEnabled: false,
    lowLatencyMode: false,
    uiAnimationReduced: false,
    bloomSuppressed: false,
    punchMode: false,
    autoCrossfade: false,
    preRollAuto: false,
    postRollAuto: false,
    waveformSmoothing: false,
    snapDisabled: false,
    autoPunchRegion: undefined,
  });

  const preparePunchRegion = useCallback((start: number, end: number) => {
    setState(prev => ({
      ...prev,
      autoPunchRegion: { start, end },
    }));
  }, []);

  const applyBrainState = useCallback((brainState: BehaviorState) => {
    const { mode, flow, tension } = brainState;
    const isPerformanceMode = mode === 'record';
    const isPunchMode = mode === 'punch';

    setState({
      // Precision Mode â†’ increase snapping (disabled in punch mode)
      snapPrecision: isPunchMode ? 0 : mode === 'edit' ? 2.0 : mode === 'flow' ? 1.5 : 1.0,

      // Flow Mode â†’ smooth scroll & zoom interpolation
      scrollSmoothing: mode === 'flow' || mode === 'burst' ? 0.8 : 0.5,

      // Creative Burst â†’ prefetch next assets
      prefetchEnabled: mode === 'burst' && flow > 0.7,

      // Performance/Recording Mode â†’ low-latency monitoring
      lowLatencyMode: isPerformanceMode || isPunchMode,

      // Performance Mode â†’ reduce UI animation noise
      uiAnimationReduced: isPerformanceMode || isPunchMode,

      // Performance Mode â†’ suppress Bloom (quiet control room)
      bloomSuppressed: isPerformanceMode || isPunchMode,

      // Punch Mode settings
      punchMode: isPunchMode,
      autoCrossfade: isPunchMode,
      preRollAuto: isPunchMode,
      postRollAuto: isPunchMode,
      waveformSmoothing: isPunchMode,
      snapDisabled: isPunchMode,
    });
  }, []);

  return (
    <SessionCoreContext.Provider value={{ state, applyBrainState, preparePunchRegion }}>
      {children}
    </SessionCoreContext.Provider>
  );
}

/**
 * AIDomain - Prime Brain, Bloom, and AI guidance
 * Phase 31: App.tsx Decomposition
 */

import React, { createContext, useContext, useState, useCallback, ReactNode } from 'react';

// ============================================================================
// Types
// ============================================================================

export type PrimeBrainMode = 'passive' | 'active' | 'learning' | 'optimizing';

export interface BloomContext {
  type: 'mix' | 'master' | 'create' | 'analyze';
  intensity: number;
}

export interface AIGuidance {
  id: string;
  message: string;
  timestamp: number;
  priority: 'low' | 'medium' | 'high';
  dismissed: boolean;
}

export interface AIState {
  primeBrainMode: PrimeBrainMode;
  bloomContext: BloomContext;
  guidanceQueue: AIGuidance[];
  isAIEnabled: boolean;
  telemetryEnabled: boolean;
}

export interface AIActions {
  setPrimeBrainMode: (mode: PrimeBrainMode) => void;
  setBloomContext: (context: BloomContext) => void;
  addGuidance: (message: string, priority?: 'low' | 'medium' | 'high') => string;
  dismissGuidance: (id: string) => void;
  clearGuidance: () => void;
  toggleAI: () => void;
  setTelemetryEnabled: (enabled: boolean) => void;
}

export interface AIDomainContextType extends AIState, AIActions {}

// ============================================================================
// Context
// ============================================================================

const AIDomainContext = createContext<AIDomainContextType | null>(null);

// ============================================================================
// Hook
// ============================================================================

export function useAI(): AIDomainContextType {
  const context = useContext(AIDomainContext);
  if (!context) {
    throw new Error('useAI must be used within AIDomainProvider');
  }
  return context;
}

// ============================================================================
// Utilities
// ============================================================================

let guidanceIdCounter = 0;
const generateGuidanceId = () => `guidance-${++guidanceIdCounter}-${Date.now().toString(36)}`;

// ============================================================================
// Provider
// ============================================================================

interface AIDomainProviderProps {
  children: ReactNode;
}

export function AIDomainProvider({ children }: AIDomainProviderProps) {
  const [primeBrainMode, setPrimeBrainModeState] = useState<PrimeBrainMode>('passive');
  const [bloomContext, setBloomContextState] = useState<BloomContext>({ type: 'mix', intensity: 0.5 });
  const [guidanceQueue, setGuidanceQueue] = useState<AIGuidance[]>([]);
  const [isAIEnabled, setIsAIEnabled] = useState(true);
  const [telemetryEnabled, setTelemetryEnabledState] = useState(false);

  // Set Prime Brain mode
  const setPrimeBrainMode = useCallback((mode: PrimeBrainMode) => {
    setPrimeBrainModeState(mode);
  }, []);

  // Set Bloom context
  const setBloomContext = useCallback((context: BloomContext) => {
    setBloomContextState(context);
  }, []);

  // Add guidance
  const addGuidance = useCallback((
    message: string, 
    priority: 'low' | 'medium' | 'high' = 'medium'
  ): string => {
    const id = generateGuidanceId();
    const guidance: AIGuidance = {
      id,
      message,
      timestamp: Date.now(),
      priority,
      dismissed: false,
    };
    setGuidanceQueue(prev => [...prev, guidance]);
    return id;
  }, []);

  // Dismiss guidance
  const dismissGuidance = useCallback((id: string) => {
    setGuidanceQueue(prev => prev.map(g => 
      g.id === id ? { ...g, dismissed: true } : g
    ));
  }, []);

  // Clear guidance
  const clearGuidance = useCallback(() => {
    setGuidanceQueue([]);
  }, []);

  // Toggle AI
  const toggleAI = useCallback(() => {
    setIsAIEnabled(prev => !prev);
  }, []);

  // Set telemetry enabled
  const setTelemetryEnabled = useCallback((enabled: boolean) => {
    setTelemetryEnabledState(enabled);
  }, []);

  const contextValue: AIDomainContextType = {
    primeBrainMode,
    bloomContext,
    guidanceQueue,
    isAIEnabled,
    telemetryEnabled,
    setPrimeBrainMode,
    setBloomContext,
    addGuidance,
    dismissGuidance,
    clearGuidance,
    toggleAI,
    setTelemetryEnabled,
  };

  return (
    <AIDomainContext.Provider value={contextValue}>
      {children}
    </AIDomainContext.Provider>
  );
}

export default AIDomainProvider;

/**
 * AudioDomain - Centralized audio context and master chain management
 * Phase 31: App.tsx Decomposition
 */

import React, { createContext, useContext, useRef, useState, useCallback, ReactNode } from 'react';
import { createSignalMatrix } from '../../audio/SignalMatrix';

// ============================================================================
// Types
// ============================================================================

export interface MasterNodes {
  input: GainNode;
  compressor: DynamicsCompressorNode;
  limiter: DynamicsCompressorNode;
  meter: AnalyserNode;
  output: GainNode;
}

export interface AudioDomainState {
  // Core audio context
  audioContext: AudioContext | null;
  isAudioReady: boolean;
  sampleRate: number;

  // Master chain
  masterNodes: MasterNodes | null;
  signalMatrix: ReturnType<typeof createSignalMatrix> | null;
  masterVolume: number;
  masterMuted: boolean;
}

export interface AudioDomainActions {
  // Audio context management
  initializeAudio: () => Promise<AudioContext>;
  closeAudio: () => Promise<void>;
  resumeAudio: () => Promise<void>;

  // Master controls
  setMasterVolume: (volume: number) => void;
  setMasterMuted: (muted: boolean) => void;

  // Getters (for components that need refs)
  getAudioContext: () => AudioContext | null;
  getSignalMatrix: () => ReturnType<typeof createSignalMatrix> | null;
  getMasterNodes: () => MasterNodes | null;
}

export interface AudioDomainContextType extends AudioDomainState, AudioDomainActions {}

// ============================================================================
// Context
// ============================================================================

const AudioDomainContext = createContext<AudioDomainContextType | null>(null);

// ============================================================================
// Hook
// ============================================================================

export function useAudioDomain(): AudioDomainContextType {
  const context = useContext(AudioDomainContext);
  if (!context) {
    throw new Error('useAudioDomain must be used within AudioDomainProvider');
  }
  return context;
}

// ============================================================================
// Provider
// ============================================================================

interface AudioDomainProviderProps {
  children: ReactNode;
}

export function AudioDomainProvider({ children }: AudioDomainProviderProps) {
  // Refs (not state - don't trigger re-renders)
  const audioContextRef = useRef<AudioContext | null>(null);
  const masterNodesRef = useRef<MasterNodes | null>(null);
  const signalMatrixRef = useRef<ReturnType<typeof createSignalMatrix> | null>(null);

  // State (triggers re-renders for UI updates)
  const [isAudioReady, setIsAudioReady] = useState(false);
  const [sampleRate, setSampleRate] = useState(48000);
  const [masterVolume, setMasterVolumeState] = useState(0.85);
  const [masterMuted, setMasterMutedState] = useState(false);

  // Initialize audio context and master chain
  const initializeAudio = useCallback(async (): Promise<AudioContext> => {
    if (audioContextRef.current && audioContextRef.current.state !== 'closed') {
      return audioContextRef.current;
    }

    // Create audio context
    const ctx = new AudioContext({ sampleRate: 48000 });
    audioContextRef.current = ctx;
    setSampleRate(ctx.sampleRate);

    // Create master nodes
    const input = ctx.createGain();
    const compressor = ctx.createDynamicsCompressor();
    const limiter = ctx.createDynamicsCompressor();
    const meter = ctx.createAnalyser();
    const output = ctx.createGain();

    // Configure compressor
    compressor.threshold.value = -24;
    compressor.knee.value = 30;
    compressor.ratio.value = 4;
    compressor.attack.value = 0.003;
    compressor.release.value = 0.25;

    // Configure limiter
    limiter.threshold.value = -1;
    limiter.knee.value = 0;
    limiter.ratio.value = 20;
    limiter.attack.value = 0.001;
    limiter.release.value = 0.1;

    // Configure meter
    meter.fftSize = 2048;

    // Chain: input -> compressor -> limiter -> meter -> output -> destination
    input.connect(compressor);
    compressor.connect(limiter);
    limiter.connect(meter);
    meter.connect(output);
    output.connect(ctx.destination);

    masterNodesRef.current = { input, compressor, limiter, meter, output };

    // Create signal matrix
    signalMatrixRef.current = createSignalMatrix(ctx, input);

    setIsAudioReady(true);
    return ctx;
  }, []);

  // Close audio context
  const closeAudio = useCallback(async () => {
    if (audioContextRef.current) {
      await audioContextRef.current.close();
      audioContextRef.current = null;
      masterNodesRef.current = null;
      signalMatrixRef.current = null;
      setIsAudioReady(false);
    }
  }, []);

  // Resume audio context (after user gesture)
  const resumeAudio = useCallback(async () => {
    if (audioContextRef.current?.state === 'suspended') {
      await audioContextRef.current.resume();
    }
  }, []);

  // Set master volume
  const setMasterVolume = useCallback((volume: number) => {
    setMasterVolumeState(volume);
    if (masterNodesRef.current && audioContextRef.current) {
      const now = audioContextRef.current.currentTime;
      masterNodesRef.current.output.gain.setTargetAtTime(volume, now, 0.01);
    }
  }, []);

  // Set master muted
  const setMasterMuted = useCallback((muted: boolean) => {
    setMasterMutedState(muted);
    if (masterNodesRef.current && audioContextRef.current) {
      const now = audioContextRef.current.currentTime;
      masterNodesRef.current.output.gain.setTargetAtTime(muted ? 0 : masterVolume, now, 0.01);
    }
  }, [masterVolume]);

  // Getters for ref access
  const getAudioContext = useCallback(() => audioContextRef.current, []);
  const getSignalMatrix = useCallback(() => signalMatrixRef.current, []);
  const getMasterNodes = useCallback(() => masterNodesRef.current, []);

  // Context value
  const contextValue: AudioDomainContextType = {
    // State
    audioContext: audioContextRef.current,
    isAudioReady,
    sampleRate,
    masterNodes: masterNodesRef.current,
    signalMatrix: signalMatrixRef.current,
    masterVolume,
    masterMuted,

    // Actions
    initializeAudio,
    closeAudio,
    resumeAudio,
    setMasterVolume,
    setMasterMuted,
    getAudioContext,
    getSignalMatrix,
    getMasterNodes,
  };

  return (
    <AudioDomainContext.Provider value={contextValue}>
      {children}
    </AudioDomainContext.Provider>
  );
}

export default AudioDomainProvider;

/**
 * MixerDomain - Mixer state, routing, and send levels
 * Phase 31: App.tsx Decomposition
 */

import React, { createContext, useContext, useState, useCallback, ReactNode } from 'react';

// ============================================================================
// Types
// ============================================================================

export interface MixerSettings {
  volume: number; // 0-1
  pan: number;    // -1 to 1
  isMuted: boolean;
  isSolo: boolean;
}

export interface SendLevel {
  busId: string;
  level: number; // 0-1
  preFader: boolean;
}

export interface MixerState {
  trackSettings: Record<string, MixerSettings>;
  sendLevels: Record<string, SendLevel[]>; // trackId -> sends
  soloedTracks: Set<string>;
  masterVolume: number;
  masterMuted: boolean;
}

export interface MixerActions {
  setTrackVolume: (trackId: string, volume: number) => void;
  setTrackPan: (trackId: string, pan: number) => void;
  setTrackMute: (trackId: string, muted: boolean) => void;
  setTrackSolo: (trackId: string, solo: boolean) => void;

  setSendLevel: (trackId: string, busId: string, level: number) => void;
  setSendPreFader: (trackId: string, busId: string, preFader: boolean) => void;

  initTrackSettings: (trackId: string) => void;
  removeTrackSettings: (trackId: string) => void;
}

export interface MixerDomainContextType extends MixerState, MixerActions {}

// ============================================================================
// Context
// ============================================================================

const MixerDomainContext = createContext<MixerDomainContextType | null>(null);

// ============================================================================
// Hook
// ============================================================================

export function useMixer(): MixerDomainContextType {
  const context = useContext(MixerDomainContext);
  if (!context) {
    throw new Error('useMixer must be used within MixerDomainProvider');
  }
  return context;
}

// ============================================================================
// Default settings
// ============================================================================

const DEFAULT_MIXER_SETTINGS: MixerSettings = {
  volume: 0.8,
  pan: 0,
  isMuted: false,
  isSolo: false,
};

// ============================================================================
// Provider
// ============================================================================

interface MixerDomainProviderProps {
  children: ReactNode;
}

export function MixerDomainProvider({ children }: MixerDomainProviderProps) {
  const [trackSettings, setTrackSettings] = useState<Record<string, MixerSettings>>({});
  const [sendLevels, setSendLevels] = useState<Record<string, SendLevel[]>>({});
  const [soloedTracks, setSoloedTracks] = useState<Set<string>>(new Set());
  const [masterVolume, setMasterVolume] = useState(0.85);
  const [masterMuted, setMasterMuted] = useState(false);

  // Initialize track settings
  const initTrackSettings = useCallback((trackId: string) => {
    setTrackSettings(prev => ({
      ...prev,
      [trackId]: { ...DEFAULT_MIXER_SETTINGS }
    }));
    setSendLevels(prev => ({
      ...prev,
      [trackId]: []
    }));
  }, []);

  // Remove track settings
  const removeTrackSettings = useCallback((trackId: string) => {
    setTrackSettings(prev => {
      const next = { ...prev };
      delete next[trackId];
      return next;
    });
    setSendLevels(prev => {
      const next = { ...prev };
      delete next[trackId];
      return next;
    });
    setSoloedTracks(prev => {
      const next = new Set(prev);
      next.delete(trackId);
      return next;
    });
  }, []);

  // Set track volume
  const setTrackVolume = useCallback((trackId: string, volume: number) => {
    setTrackSettings(prev => ({
      ...prev,
      [trackId]: { ...(prev[trackId] || DEFAULT_MIXER_SETTINGS), volume: Math.max(0, Math.min(1, volume)) }
    }));
  }, []);

  // Set track pan
  const setTrackPan = useCallback((trackId: string, pan: number) => {
    setTrackSettings(prev => ({
      ...prev,
      [trackId]: { ...(prev[trackId] || DEFAULT_MIXER_SETTINGS), pan: Math.max(-1, Math.min(1, pan)) }
    }));
  }, []);

  // Set track mute
  const setTrackMute = useCallback((trackId: string, muted: boolean) => {
    setTrackSettings(prev => ({
      ...prev,
      [trackId]: { ...(prev[trackId] || DEFAULT_MIXER_SETTINGS), isMuted: muted }
    }));
  }, []);

  // Set track solo
  const setTrackSolo = useCallback((trackId: string, solo: boolean) => {
    setTrackSettings(prev => ({
      ...prev,
      [trackId]: { ...(prev[trackId] || DEFAULT_MIXER_SETTINGS), isSolo: solo }
    }));
    setSoloedTracks(prev => {
      const next = new Set(prev);
      if (solo) {
        next.add(trackId);
      } else {
        next.delete(trackId);
      }
      return next;
    });
  }, []);

  // Set send level
  const setSendLevel = useCallback((trackId: string, busId: string, level: number) => {
    setSendLevels(prev => {
      const trackSends = prev[trackId] || [];
      const existingIndex = trackSends.findIndex(s => s.busId === busId);
      if (existingIndex >= 0) {
        const updated = [...trackSends];
        updated[existingIndex] = { ...updated[existingIndex], level };
        return { ...prev, [trackId]: updated };
      } else {
        return { 
          ...prev, 
          [trackId]: [...trackSends, { busId, level, preFader: false }] 
        };
      }
    });
  }, []);

  // Set send pre-fader
  const setSendPreFader = useCallback((trackId: string, busId: string, preFader: boolean) => {
    setSendLevels(prev => {
      const trackSends = prev[trackId] || [];
      const updated = trackSends.map(s => 
        s.busId === busId ? { ...s, preFader } : s
      );
      return { ...prev, [trackId]: updated };
    });
  }, []);

  const contextValue: MixerDomainContextType = {
    trackSettings,
    sendLevels,
    soloedTracks,
    masterVolume,
    masterMuted,
    setTrackVolume,
    setTrackPan,
    setTrackMute,
    setTrackSolo,
    setSendLevel,
    setSendPreFader,
    initTrackSettings,
    removeTrackSettings,
  };

  return (
    <MixerDomainContext.Provider value={contextValue}>
      {children}
    </MixerDomainContext.Provider>
  );
}

export default MixerDomainProvider;

/**
 * PluginsDomain - FX windows, presets, and plugin state
 * Phase 31: App.tsx Decomposition
 */

import React, { createContext, useContext, useState, useCallback, ReactNode } from 'react';

// ============================================================================
// Types
// ============================================================================

export interface FXWindowState {
  id: string;
  trackId: string;
  pluginId: string;
  isOpen: boolean;
  position: { x: number; y: number };
  params: Record<string, number>;
}

export interface PluginPreset {
  id: string;
  name: string;
  pluginId: string;
  params: Record<string, number>;
  createdAt: number;
}

export interface PluginsState {
  openFxWindows: FXWindowState[];
  presets: Record<string, PluginPreset[]>; // pluginId -> presets
  favorites: string[]; // pluginIds
}

export interface PluginsActions {
  openFxWindow: (trackId: string, pluginId: string, position?: { x: number; y: number }) => string;
  closeFxWindow: (windowId: string) => void;
  updateFxWindowParams: (windowId: string, params: Record<string, number>) => void;
  updateFxWindowPosition: (windowId: string, position: { x: number; y: number }) => void;

  savePreset: (pluginId: string, name: string, params: Record<string, number>) => string;
  loadPreset: (presetId: string) => PluginPreset | null;
  deletePreset: (presetId: string) => void;

  toggleFavorite: (pluginId: string) => void;
  isFavorite: (pluginId: string) => boolean;
}

export interface PluginsDomainContextType extends PluginsState, PluginsActions {}

// ============================================================================
// Context
// ============================================================================

const PluginsDomainContext = createContext<PluginsDomainContextType | null>(null);

// ============================================================================
// Hook
// ============================================================================

export function usePlugins(): PluginsDomainContextType {
  const context = useContext(PluginsDomainContext);
  if (!context) {
    throw new Error('usePlugins must be used within PluginsDomainProvider');
  }
  return context;
}

// ============================================================================
// Utilities
// ============================================================================

let windowIdCounter = 0;
const generateWindowId = () => `fx-window-${++windowIdCounter}-${Date.now().toString(36)}`;

let presetIdCounter = 0;
const generatePresetId = () => `preset-${++presetIdCounter}-${Date.now().toString(36)}`;

// ============================================================================
// Provider
// ============================================================================

interface PluginsDomainProviderProps {
  children: ReactNode;
}

export function PluginsDomainProvider({ children }: PluginsDomainProviderProps) {
  const [openFxWindows, setOpenFxWindows] = useState<FXWindowState[]>([]);
  const [presets, setPresets] = useState<Record<string, PluginPreset[]>>({});
  const [favorites, setFavorites] = useState<string[]>([]);

  // Open FX window
  const openFxWindow = useCallback((
    trackId: string, 
    pluginId: string, 
    position = { x: 100, y: 100 }
  ): string => {
    const id = generateWindowId();
    const newWindow: FXWindowState = {
      id,
      trackId,
      pluginId,
      isOpen: true,
      position,
      params: {},
    };
    setOpenFxWindows(prev => [...prev, newWindow]);
    return id;
  }, []);

  // Close FX window
  const closeFxWindow = useCallback((windowId: string) => {
    setOpenFxWindows(prev => prev.filter(w => w.id !== windowId));
  }, []);

  // Update FX window params
  const updateFxWindowParams = useCallback((windowId: string, params: Record<string, number>) => {
    setOpenFxWindows(prev => prev.map(w => 
      w.id === windowId ? { ...w, params: { ...w.params, ...params } } : w
    ));
  }, []);

  // Update FX window position
  const updateFxWindowPosition = useCallback((windowId: string, position: { x: number; y: number }) => {
    setOpenFxWindows(prev => prev.map(w => 
      w.id === windowId ? { ...w, position } : w
    ));
  }, []);

  // Save preset
  const savePreset = useCallback((
    pluginId: string, 
    name: string, 
    params: Record<string, number>
  ): string => {
    const id = generatePresetId();
    const preset: PluginPreset = {
      id,
      name,
      pluginId,
      params,
      createdAt: Date.now(),
    };
    setPresets(prev => ({
      ...prev,
      [pluginId]: [...(prev[pluginId] || []), preset]
    }));
    return id;
  }, []);

  // Load preset
  const loadPreset = useCallback((presetId: string): PluginPreset | null => {
    for (const pluginPresets of Object.values(presets)) {
      const preset = pluginPresets.find(p => p.id === presetId);
      if (preset) return preset;
    }
    return null;
  }, [presets]);

  // Delete preset
  const deletePreset = useCallback((presetId: string) => {
    setPresets(prev => {
      const next: Record<string, PluginPreset[]> = {};
      for (const [pluginId, pluginPresets] of Object.entries(prev)) {
        next[pluginId] = pluginPresets.filter(p => p.id !== presetId);
      }
      return next;
    });
  }, []);

  // Toggle favorite
  const toggleFavorite = useCallback((pluginId: string) => {
    setFavorites(prev => 
      prev.includes(pluginId) 
        ? prev.filter(id => id !== pluginId)
        : [...prev, pluginId]
    );
  }, []);

  // Is favorite
  const isFavorite = useCallback((pluginId: string): boolean => {
    return favorites.includes(pluginId);
  }, [favorites]);

  const contextValue: PluginsDomainContextType = {
    openFxWindows,
    presets,
    favorites,
    openFxWindow,
    closeFxWindow,
    updateFxWindowParams,
    updateFxWindowPosition,
    savePreset,
    loadPreset,
    deletePreset,
    toggleFavorite,
    isFavorite,
  };

  return (
    <PluginsDomainContext.Provider value={contextValue}>
      {children}
    </PluginsDomainContext.Provider>
  );
}

export default PluginsDomainProvider;

/**
 * TracksDomain - Track state and CRUD operations
 * Phase 31: App.tsx Decomposition
 */

import React, { createContext, useContext, useState, useCallback, useRef, ReactNode } from 'react';

// ============================================================================
// Types
// ============================================================================

export type TrackRole = 'vocals' | 'drums' | 'bass' | 'guitar' | 'piano' | 'synths' | 'strings' | 'other';
export type TrackColor = 'cyan' | 'magenta' | 'blue' | 'green' | 'purple' | 'crimson';
export type TrackGroup = 'Vocals' | 'Harmony' | 'Adlibs' | 'Bass' | 'Drums' | 'Instruments';
export type WaveformType = 'dense' | 'sparse' | 'varied' | 'bass';

export interface Track {
  id: string;
  trackName: string;
  trackColor: TrackColor;
  waveformType: WaveformType;
  group: TrackGroup;
  role: TrackRole;
  isProcessing?: boolean;
  locked?: boolean;
}

export interface Clip {
  id: string;
  trackId: string;
  startTime: number;
  duration: number;
  buffer?: AudioBuffer;
  name: string;
  offset: number;
}

export interface TracksState {
  tracks: Track[];
  clips: Record<string, Clip[]>; // trackId -> clips
  selectedTrackId: string | null;
  selectedClipIds: string[];
}

export interface TracksActions {
  addTrack: (track: Omit<Track, 'id'>) => string;
  removeTrack: (trackId: string) => void;
  updateTrack: (trackId: string, updates: Partial<Track>) => void;
  selectTrack: (trackId: string | null) => void;

  addClip: (trackId: string, clip: Omit<Clip, 'id'>) => string;
  removeClip: (clipId: string) => void;
  updateClip: (clipId: string, updates: Partial<Clip>) => void;
  selectClips: (clipIds: string[]) => void;

  reorderTracks: (fromIndex: number, toIndex: number) => void;
}

export interface TracksDomainContextType extends TracksState, TracksActions {}

// ============================================================================
// Context
// ============================================================================

const TracksDomainContext = createContext<TracksDomainContextType | null>(null);

// ============================================================================
// Hook
// ============================================================================

export function useTracks(): TracksDomainContextType {
  const context = useContext(TracksDomainContext);
  if (!context) {
    throw new Error('useTracks must be used within TracksDomainProvider');
  }
  return context;
}

// ============================================================================
// Utilities
// ============================================================================

let trackIdCounter = 0;
const generateTrackId = () => `track-${++trackIdCounter}-${Date.now().toString(36)}`;

let clipIdCounter = 0;
const generateClipId = () => `clip-${++clipIdCounter}-${Date.now().toString(36)}`;

// ============================================================================
// Provider
// ============================================================================

interface TracksDomainProviderProps {
  children: ReactNode;
}

export function TracksDomainProvider({ children }: TracksDomainProviderProps) {
  const [tracks, setTracks] = useState<Track[]>([]);
  const [clips, setClips] = useState<Record<string, Clip[]>>({});
  const [selectedTrackId, setSelectedTrackId] = useState<string | null>(null);
  const [selectedClipIds, setSelectedClipIds] = useState<string[]>([]);

  // Add track
  const addTrack = useCallback((trackData: Omit<Track, 'id'>): string => {
    const id = generateTrackId();
    const track: Track = { ...trackData, id };
    setTracks(prev => [...prev, track]);
    setClips(prev => ({ ...prev, [id]: [] }));
    return id;
  }, []);

  // Remove track
  const removeTrack = useCallback((trackId: string) => {
    setTracks(prev => prev.filter(t => t.id !== trackId));
    setClips(prev => {
      const next = { ...prev };
      delete next[trackId];
      return next;
    });
    if (selectedTrackId === trackId) {
      setSelectedTrackId(null);
    }
  }, [selectedTrackId]);

  // Update track
  const updateTrack = useCallback((trackId: string, updates: Partial<Track>) => {
    setTracks(prev => prev.map(t => 
      t.id === trackId ? { ...t, ...updates } : t
    ));
  }, []);

  // Select track
  const selectTrack = useCallback((trackId: string | null) => {
    setSelectedTrackId(trackId);
  }, []);

  // Add clip
  const addClip = useCallback((trackId: string, clipData: Omit<Clip, 'id'>): string => {
    const id = generateClipId();
    const clip: Clip = { ...clipData, id };
    setClips(prev => ({
      ...prev,
      [trackId]: [...(prev[trackId] || []), clip]
    }));
    return id;
  }, []);

  // Remove clip
  const removeClip = useCallback((clipId: string) => {
    setClips(prev => {
      const next: Record<string, Clip[]> = {};
      for (const [trackId, trackClips] of Object.entries(prev)) {
        next[trackId] = trackClips.filter(c => c.id !== clipId);
      }
      return next;
    });
    setSelectedClipIds(prev => prev.filter(id => id !== clipId));
  }, []);

  // Update clip
  const updateClip = useCallback((clipId: string, updates: Partial<Clip>) => {
    setClips(prev => {
      const next: Record<string, Clip[]> = {};
      for (const [trackId, trackClips] of Object.entries(prev)) {
        next[trackId] = trackClips.map(c => 
          c.id === clipId ? { ...c, ...updates } : c
        );
      }
      return next;
    });
  }, []);

  // Select clips
  const selectClips = useCallback((clipIds: string[]) => {
    setSelectedClipIds(clipIds);
  }, []);

  // Reorder tracks
  const reorderTracks = useCallback((fromIndex: number, toIndex: number) => {
    setTracks(prev => {
      const next = [...prev];
      const [removed] = next.splice(fromIndex, 1);
      next.splice(toIndex, 0, removed);
      return next;
    });
  }, []);

  const contextValue: TracksDomainContextType = {
    tracks,
    clips,
    selectedTrackId,
    selectedClipIds,
    addTrack,
    removeTrack,
    updateTrack,
    selectTrack,
    addClip,
    removeClip,
    updateClip,
    selectClips,
    reorderTracks,
  };

  return (
    <TracksDomainContext.Provider value={contextValue}>
      {children}
    </TracksDomainContext.Provider>
  );
}

export default TracksDomainProvider;

/**
 * TransportDomain - Playback, timeline, and tempo management
 * Phase 31: App.tsx Decomposition
 */

import React, { createContext, useContext, useState, useCallback, useRef, ReactNode } from 'react';

// ============================================================================
// Types
// ============================================================================

export interface TransportState {
  isPlaying: boolean;
  isPaused: boolean;
  isRecording: boolean;
  playheadPosition: number; // in seconds
  loopStart: number;
  loopEnd: number;
  loopEnabled: boolean;
  tempo: number; // BPM
  timeSignature: [number, number]; // [beats, noteValue] e.g., [4, 4]
  currentBeat: number;
  currentBar: number;
}

export interface TransportActions {
  play: () => void;
  pause: () => void;
  stop: () => void;
  toggleRecord: () => void;
  seek: (position: number) => void;
  setTempo: (bpm: number) => void;
  setTimeSignature: (beats: number, noteValue: number) => void;
  setLoop: (start: number, end: number) => void;
  toggleLoop: () => void;
}

export interface TransportDomainContextType extends TransportState, TransportActions {}

// ============================================================================
// Context
// ============================================================================

const TransportDomainContext = createContext<TransportDomainContextType | null>(null);

// ============================================================================
// Hook
// ============================================================================

export function useTransport(): TransportDomainContextType {
  const context = useContext(TransportDomainContext);
  if (!context) {
    throw new Error('useTransport must be used within TransportDomainProvider');
  }
  return context;
}

// ============================================================================
// Provider
// ============================================================================

interface TransportDomainProviderProps {
  children: ReactNode;
  audioContext?: AudioContext | null;
}

export function TransportDomainProvider({ children, audioContext }: TransportDomainProviderProps) {
  // State
  const [isPlaying, setIsPlaying] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [isRecording, setIsRecording] = useState(false);
  const [playheadPosition, setPlayheadPosition] = useState(0);
  const [loopStart, setLoopStart] = useState(0);
  const [loopEnd, setLoopEnd] = useState(0);
  const [loopEnabled, setLoopEnabled] = useState(false);
  const [tempo, setTempoState] = useState(120);
  const [timeSignature, setTimeSignatureState] = useState<[number, number]>([4, 4]);
  const [currentBeat, setCurrentBeat] = useState(1);
  const [currentBar, setCurrentBar] = useState(1);

  // Refs for animation frame
  const animationFrameRef = useRef<number | null>(null);
  const startTimeRef = useRef<number>(0);

  // Play
  const play = useCallback(() => {
    setIsPlaying(true);
    setIsPaused(false);
    if (audioContext) {
      startTimeRef.current = audioContext.currentTime - playheadPosition;
    }
  }, [audioContext, playheadPosition]);

  // Pause
  const pause = useCallback(() => {
    setIsPlaying(false);
    setIsPaused(true);
    if (animationFrameRef.current) {
      cancelAnimationFrame(animationFrameRef.current);
    }
  }, []);

  // Stop
  const stop = useCallback(() => {
    setIsPlaying(false);
    setIsPaused(false);
    setPlayheadPosition(0);
    setCurrentBeat(1);
    setCurrentBar(1);
    if (animationFrameRef.current) {
      cancelAnimationFrame(animationFrameRef.current);
    }
  }, []);

  // Toggle record
  const toggleRecord = useCallback(() => {
    setIsRecording(prev => !prev);
  }, []);

  // Seek
  const seek = useCallback((position: number) => {
    setPlayheadPosition(Math.max(0, position));
    if (audioContext && isPlaying) {
      startTimeRef.current = audioContext.currentTime - position;
    }
  }, [audioContext, isPlaying]);

  // Set tempo
  const setTempo = useCallback((bpm: number) => {
    setTempoState(Math.max(20, Math.min(300, bpm)));
  }, []);

  // Set time signature
  const setTimeSignature = useCallback((beats: number, noteValue: number) => {
    setTimeSignatureState([beats, noteValue]);
  }, []);

  // Set loop
  const setLoop = useCallback((start: number, end: number) => {
    setLoopStart(start);
    setLoopEnd(end);
  }, []);

  // Toggle loop
  const toggleLoop = useCallback(() => {
    setLoopEnabled(prev => !prev);
  }, []);

  // Context value
  const contextValue: TransportDomainContextType = {
    // State
    isPlaying,
    isPaused,
    isRecording,
    playheadPosition,
    loopStart,
    loopEnd,
    loopEnabled,
    tempo,
    timeSignature,
    currentBeat,
    currentBar,

    // Actions
    play,
    pause,
    stop,
    toggleRecord,
    seek,
    setTempo,
    setTimeSignature,
    setLoop,
    toggleLoop,
  };

  return (
    <TransportDomainContext.Provider value={contextValue}>
      {children}
    </TransportDomainContext.Provider>
  );
}

export default TransportDomainProvider;

/**
 * UIDomain - Theme, layout, and dock positions
 * Phase 31: App.tsx Decomposition
 */

import React, { createContext, useContext, useState, useCallback, ReactNode } from 'react';

// ============================================================================
// Types
// ============================================================================

export interface Point {
  x: number;
  y: number;
}

export interface DockState {
  position: Point;
  isVisible: boolean;
  isExpanded: boolean;
}

export interface UILayout {
  panelViewEnabled: boolean;
  mixerVisible: boolean;
  timelineVisible: boolean;
  pianoRollVisible: boolean;
  aiHubVisible: boolean;
  browserVisible: boolean;
}

export interface UIState {
  theme: 'dark' | 'light' | 'aura';
  dockState: DockState;
  hubPosition: Point;
  layout: UILayout;
  activePanel: string | null;
  isFullscreen: boolean;
}

export interface UIActions {
  setTheme: (theme: 'dark' | 'light' | 'aura') => void;
  setDockPosition: (position: Point) => void;
  setDockVisible: (visible: boolean) => void;
  setDockExpanded: (expanded: boolean) => void;
  setHubPosition: (position: Point) => void;
  setLayoutPanel: (panel: keyof UILayout, visible: boolean) => void;
  setActivePanel: (panel: string | null) => void;
  toggleFullscreen: () => void;
}

export interface UIDomainContextType extends UIState, UIActions {}

// ============================================================================
// Context
// ============================================================================

const UIDomainContext = createContext<UIDomainContextType | null>(null);

// ============================================================================
// Hook
// ============================================================================

export function useUI(): UIDomainContextType {
  const context = useContext(UIDomainContext);
  if (!context) {
    throw new Error('useUI must be used within UIDomainProvider');
  }
  return context;
}

// ============================================================================
// Defaults
// ============================================================================

const DEFAULT_DOCK_STATE: DockState = {
  position: { x: window.innerWidth / 2 - 150, y: window.innerHeight - 100 },
  isVisible: true,
  isExpanded: false,
};

const DEFAULT_LAYOUT: UILayout = {
  panelViewEnabled: true,
  mixerVisible: true,
  timelineVisible: true,
  pianoRollVisible: false,
  aiHubVisible: false,
  browserVisible: false,
};

// ============================================================================
// Provider
// ============================================================================

interface UIDomainProviderProps {
  children: ReactNode;
}

export function UIDomainProvider({ children }: UIDomainProviderProps) {
  const [theme, setThemeState] = useState<'dark' | 'light' | 'aura'>('aura');
  const [dockState, setDockState] = useState<DockState>(DEFAULT_DOCK_STATE);
  const [hubPosition, setHubPositionState] = useState<Point>({ x: 100, y: 100 });
  const [layout, setLayout] = useState<UILayout>(DEFAULT_LAYOUT);
  const [activePanel, setActivePanelState] = useState<string | null>(null);
  const [isFullscreen, setIsFullscreen] = useState(false);

  // Set theme
  const setTheme = useCallback((newTheme: 'dark' | 'light' | 'aura') => {
    setThemeState(newTheme);
  }, []);

  // Set dock position
  const setDockPosition = useCallback((position: Point) => {
    setDockState(prev => ({ ...prev, position }));
  }, []);

  // Set dock visible
  const setDockVisible = useCallback((visible: boolean) => {
    setDockState(prev => ({ ...prev, isVisible: visible }));
  }, []);

  // Set dock expanded
  const setDockExpanded = useCallback((expanded: boolean) => {
    setDockState(prev => ({ ...prev, isExpanded: expanded }));
  }, []);

  // Set hub position
  const setHubPosition = useCallback((position: Point) => {
    setHubPositionState(position);
  }, []);

  // Set layout panel visibility
  const setLayoutPanel = useCallback((panel: keyof UILayout, visible: boolean) => {
    setLayout(prev => ({ ...prev, [panel]: visible }));
  }, []);

  // Set active panel
  const setActivePanel = useCallback((panel: string | null) => {
    setActivePanelState(panel);
  }, []);

  // Toggle fullscreen
  const toggleFullscreen = useCallback(() => {
    setIsFullscreen(prev => !prev);
    if (!document.fullscreenElement) {
      document.documentElement.requestFullscreen();
    } else {
      document.exitFullscreen();
    }
  }, []);

  const contextValue: UIDomainContextType = {
    theme,
    dockState,
    hubPosition,
    layout,
    activePanel,
    isFullscreen,
    setTheme,
    setDockPosition,
    setDockVisible,
    setDockExpanded,
    setHubPosition,
    setLayoutPanel,
    setActivePanel,
    toggleFullscreen,
  };

  return (
    <UIDomainContext.Provider value={contextValue}>
      {children}
    </UIDomainContext.Provider>
  );
}

export default UIDomainProvider;

/**
 * useDomainBridge - Hook to bridge FlowRuntime state with Domain contexts
 * Phase 31.2: State Migration
 * 
 * This hook provides a gradual migration path by syncing FlowRuntime state
 * with the new domain contexts. Components can start using domain hooks
 * while FlowRuntime still manages the actual state.
 * 
 * Usage:
 * 1. Wrap app in DomainBridge
 * 2. Call useDomainBridge() in FlowRuntime to sync state
 * 3. Child components can use useAudioDomain(), useTracks(), etc.
 */

import { useEffect, useCallback } from 'react';
import { useAudioDomain } from './audio';
import { useTransport } from './transport';
import { useTracks } from './tracks';
import { useMixer } from './mixer';
import { usePlugins } from './plugins';
import { useAI } from './ai';
import { useUI } from './ui';

// Type for FlowRuntime state that we'll sync from
export interface FlowRuntimeState {
  // Transport
  isPlaying: boolean;
  currentTime: number;
  bpm: number;
  isLooping: boolean;

  // Tracks
  tracks: Array<{
    id: string;
    trackName: string;
    trackColor: string;
    role: string;
    group: string;
  }>;

  // Mixer
  mixerSettings: Record<string, { volume: number; pan: number; isMuted: boolean }>;
  soloedTracks: Set<string>;
  masterVolume: number;

  // Audio
  masterReady: boolean;
}

/**
 * Syncs FlowRuntime state with domain contexts
 * Call this in FlowRuntime to enable child components to use domain hooks
 */
export function useDomainBridgeSync(state: Partial<FlowRuntimeState>) {
  // Get domain contexts - these will throw if not wrapped in DomainBridge
  // Use try/catch for graceful degradation during migration
  let transport: ReturnType<typeof useTransport> | null = null;
  let tracks: ReturnType<typeof useTracks> | null = null;
  let mixer: ReturnType<typeof useMixer> | null = null;

  try {
    transport = useTransport();
  } catch {
    // Not wrapped in DomainBridge yet - that's OK during migration
  }

  try {
    tracks = useTracks();
  } catch {
    // Not wrapped in DomainBridge yet
  }

  try {
    mixer = useMixer();
  } catch {
    // Not wrapped in DomainBridge yet
  }

  // Sync transport state
  useEffect(() => {
    if (!transport) return;

    if (state.isPlaying !== undefined) {
      if (state.isPlaying && !transport.isPlaying) {
        transport.play();
      } else if (!state.isPlaying && transport.isPlaying) {
        transport.pause();
      }
    }

    if (state.bpm !== undefined && state.bpm !== transport.tempo) {
      transport.setTempo(state.bpm);
    }

    if (state.isLooping !== undefined && state.isLooping !== transport.loopEnabled) {
      transport.toggleLoop();
    }
  }, [transport, state.isPlaying, state.bpm, state.isLooping]);

  // Sync mixer state
  useEffect(() => {
    if (!mixer || !state.mixerSettings) return;

    Object.entries(state.mixerSettings).forEach(([trackId, settings]) => {
      const current = mixer.trackSettings[trackId];
      if (!current) {
        mixer.initTrackSettings(trackId);
      }
      if (current?.volume !== settings.volume) {
        mixer.setTrackVolume(trackId, settings.volume);
      }
      if (current?.pan !== settings.pan) {
        mixer.setTrackPan(trackId, settings.pan);
      }
      if (current?.isMuted !== settings.isMuted) {
        mixer.setTrackMute(trackId, settings.isMuted);
      }
    });
  }, [mixer, state.mixerSettings]);

  return {
    transport,
    tracks,
    mixer,
    isBridgeActive: !!(transport || tracks || mixer),
  };
}

/**
 * Simplified hook for components that just need to check if domains are available
 */
export function useDomainStatus() {
  let hasAudio = false;
  let hasTransport = false;
  let hasTracks = false;
  let hasMixer = false;
  let hasPlugins = false;
  let hasAI = false;
  let hasUI = false;

  try { useAudioDomain(); hasAudio = true; } catch { /* not available */ }
  try { useTransport(); hasTransport = true; } catch { /* not available */ }
  try { useTracks(); hasTracks = true; } catch { /* not available */ }
  try { useMixer(); hasMixer = true; } catch { /* not available */ }
  try { usePlugins(); hasPlugins = true; } catch { /* not available */ }
  try { useAI(); hasAI = true; } catch { /* not available */ }
  try { useUI(); hasUI = true; } catch { /* not available */ }

  return {
    hasAudio,
    hasTransport,
    hasTracks,
    hasMixer,
    hasPlugins,
    hasAI,
    hasUI,
    allActive: hasAudio && hasTransport && hasTracks && hasMixer && hasPlugins && hasAI && hasUI,
  };
}

export default useDomainBridgeSync;

import React, { createContext, useContext, useState, useEffect, useCallback, useRef } from 'react';
import { NexusRule, NexusContext, NexusTrigger, NexusAction } from './NexusTypes';
import { FourAnchors } from '../types/sonic-architecture';
import { VelvetLoudnessMetrics } from '../audio/VelvetLoudnessMeter';
import { useTimelineStore } from '../state/timelineStore';

const NexusLogicContext = createContext<NexusContext | undefined>(undefined);

export const useNexusLogic = () => {
  const context = useContext(NexusLogicContext);
  if (!context) {
    throw new Error('useNexusLogic must be used within a NexusLogicProvider');
  }
  return context;
};

interface NexusLogicProviderProps {
  children: React.ReactNode;
  transportTime: number;
  isPlaying: boolean;
  onAction: (action: NexusAction) => void;
  masterAnalysis?: FourAnchors | null;
  loudnessMetrics?: VelvetLoudnessMetrics | null;
  bpm: number;
}

export const NexusLogicProvider: React.FC<NexusLogicProviderProps> = ({
  children,
  transportTime,
  isPlaying,
  onAction,
  masterAnalysis,
  loudnessMetrics,
  bpm,
}) => {
  const { markers, clips } = useTimelineStore();
  const [rules, setRules] = useState<NexusRule[]>([]);
  const [trends, setTrends] = useState<Record<string, 'UP' | 'DOWN' | 'STABLE'>>({});
  const [registers, setRegisters] = useState<Record<string, any>>({});
  const intentsRef = useRef<Set<string>>(new Set());
  const metricHistoryRef = useRef<Record<string, { time: number; value: number }[]>>({});
  const lastEvaluatedTimeRef = useRef<number>(0);

  // Phase 7: Intent Registry
  const registerIntent = useCallback((intent: string) => {
    intentsRef.current.add(intent);
    // Intents last for 5 seconds by default if not cleared
    setTimeout(() => {
      intentsRef.current.delete(intent);
    }, 5000);
    console.log(`[Nexus] Intent registered: ${intent}`);
  }, []);

  const setRegister = useCallback((key: string, value: any) => {
    setRegisters(prev => ({ ...prev, [key]: value }));
    console.log(`[Nexus] Register updated: ${key} = ${value}`);
  }, []);

  // Phase 6: Metric History Tracking & Trend Calculation
  useEffect(() => {
    if (!masterAnalysis && !loudnessMetrics) return;
    const now = transportTime;

    const updateHistory = (type: string, val: number) => {
      if (!metricHistoryRef.current[type]) metricHistoryRef.current[type] = [];
      const history = metricHistoryRef.current[type];
      history.push({ time: now, value: val });

      if (history.length > 50) {
        metricHistoryRef.current[type] = history.filter(h => now - h.time < 10);
      }
    };

    if (loudnessMetrics) {
      updateHistory('LUFS_INTEGRATED', loudnessMetrics.integratedLUFS);
      updateHistory('LUFS_MOMENTARY', loudnessMetrics.momentaryLUFS);
    }
    if (masterAnalysis) {
      updateHistory('BODY', masterAnalysis.body);
      updateHistory('PRESENCE', masterAnalysis.soul);
      updateHistory('AIRINESS', masterAnalysis.air);
      if ((masterAnalysis as any).transientDensity) {
        updateHistory('TRANSIENT_DENSITY', (masterAnalysis as any).transientDensity);
      }
    }

    // Calculate Trends every second or so
    const calculateTrend = (type: string) => {
      const history = metricHistoryRef.current[type] || [];
      if (history.length < 10) return 'STABLE';

      const recent = history.slice(-5);
      const past = history.slice(-15, -10);

      if (past.length === 0) return 'STABLE';

      const recentAvg = recent.reduce((sum, h) => sum + h.value, 0) / recent.length;
      const pastAvg = past.reduce((sum, h) => sum + h.value, 0) / past.length;
      const diff = recentAvg - pastAvg;

      // Thresholds based on metric type
      const threshold = (type.includes('LUFS')) ? 0.3 : 1.5;

      if (diff > threshold) return 'UP';
      if (diff < -threshold) return 'DOWN';
      return 'STABLE';
    };

    setTrends({
      LUFS_INTEGRATED: calculateTrend('LUFS_INTEGRATED') as any,
      PRESENCE: calculateTrend('PRESENCE') as any,
      AIRINESS: calculateTrend('AIRINESS') as any,
      BODY: calculateTrend('BODY') as any,
      TRANSIENT_DENSITY: calculateTrend('TRANSIENT_DENSITY') as any,
    });

  }, [transportTime, masterAnalysis, loudnessMetrics]);

  const addRule = useCallback((rule: NexusRule) => {
    // Initialize adaptation state
    const adaptiveRule = {
      ...rule,
      confidenceScore: rule.confidenceScore ?? 1.0,
      enabled: rule.enabled ?? true
    };
    setRules((prev) => [...prev, adaptiveRule]);
  }, []);

  const removeRule = useCallback((id: string) => {
    setRules((prev) => prev.filter((r) => r.id !== id));
  }, []);

  const toggleRule = useCallback((id: string) => {
    setRules((prev) =>
      prev.map((r) => (r.id === id ? { ...r, enabled: !r.enabled } : r))
    );
  }, []);

  // Phase 6: User-Override Learning
  const rejectRule = useCallback((id: string) => {
    setRules((prev) =>
      prev.map((r) => {
        if (r.id === id) {
          const newScore = Math.max(0, (r.confidenceScore ?? 1.0) - 0.2);
          return { 
            ...r, 
            confidenceScore: newScore,
            // Inhibit for 30 seconds
            suppressUntil: Date.now() + 30000,
            // Auto-disable if confidence is critically low
            enabled: newScore > 0.3 ? r.enabled : false
          };
        }
        return r;
      })
    );
    console.log(`[Nexus] Rule ${id} rejected. Confidence decreased.`);
  }, []);

  // Evaluation Loop
  useEffect(() => {
    if (!isPlaying) return;

    const secondsPerBeat = 60 / bpm;
    const currentBeat = Math.floor(transportTime / secondsPerBeat);

    // Revised Helper for Phase 7: Synapse Evaluation
    const evaluateTrigger = (trigger: NexusTrigger, rule: NexusRule): boolean => {
      let triggered = false;
      const { lastFired, suppressUntil } = rule;

      // Phase 6: Inhibition Logic
      if (suppressUntil && Date.now() < suppressUntil) return false;

      // Phase 7: Register Condition Check
      if (trigger.params.register) {
        const val = registers[trigger.params.register];
        if (val !== trigger.params.registerValue) return false;
      }

      switch (trigger.type) {
        case 'AGENT_INTENT': {
          if (trigger.params.intent) {
            triggered = intentsRef.current.has(trigger.params.intent);
          }
          break;
        }
        case 'BEAT': {
          const targetBeat = trigger.params.beat ?? 0;

          // Phase 6: Organic Variance (Non-deterministic Timing)
          const jitter = trigger.params.variance ? (Math.random() - 0.5) * 2 * trigger.params.variance * 200 : 0;
          const leadTimeSeconds = ((trigger.params.leadTimeMs ?? 0) + jitter) / 1000;

          const targetTime = targetBeat * secondsPerBeat;

          if (transportTime >= targetTime - leadTimeSeconds && 
              transportTime < targetTime + 0.1 && 
              lastFired !== targetBeat) {
            triggered = true;
          }
          break;
        }
        case 'SONIC_METRIC': {
          if (!masterAnalysis && !loudnessMetrics) return false;

          let currentValue = 0;
          const { metricType, threshold, operator, snapToGrid, deltaThreshold, deltaWindowMs } = trigger.params;
          const type = metricType || 'LUFS_INTEGRATED';

          if (type === 'LUFS_INTEGRATED') currentValue = loudnessMetrics?.integratedLUFS ?? -Infinity;
          else if (type === 'LUFS_MOMENTARY') currentValue = loudnessMetrics?.momentaryLUFS ?? -Infinity;
          else if (type === 'BODY') currentValue = masterAnalysis?.body ?? 0;
          else if (type === 'PRESENCE') currentValue = masterAnalysis?.soul ?? 0;
          else if (type === 'AIRINESS') currentValue = masterAnalysis?.air ?? 0;
          else if (type === 'TRANSIENT_DENSITY') currentValue = (masterAnalysis as any)?.transientDensity ?? 0;

          // Phase 6: Trend-Based Differential Analysis
          if (deltaThreshold && deltaWindowMs) {
            const history = metricHistoryRef.current[type] || [];
            const lookbackTime = transportTime - (deltaWindowMs / 1000);
            const pastEntry = history.slice().reverse().find(h => h.time <= lookbackTime);

            if (pastEntry) {
              const delta = currentValue - pastEntry.value;
              triggered = Math.abs(delta) >= deltaThreshold;
            }
          } else {
            // Standard static threshold
            const thresh = threshold ?? 0;
            if (operator === '>') triggered = currentValue > thresh;
            else if (operator === '<') triggered = currentValue < thresh;
            else if (operator === '>=') triggered = currentValue >= thresh;
          }

          if (triggered && snapToGrid) {
            const isOnBeat = Math.abs(transportTime % secondsPerBeat) < 0.05;
            if (!isOnBeat) triggered = false;
          }

          if (triggered && lastFired && Date.now() - (lastFired as number) < 500) {
            triggered = false;
          }
          break;
        }
        case 'ARRANGEMENT_LOOKAHEAD': {
          const { lookaheadMs, targetMarker, eventType } = trigger.params;
          const lookaheadSec = (lookaheadMs ?? 0) / 1000;

          if (eventType === 'MARKER') {
            const upcomingMarker = markers.find(m => {
              const timeToEvent = m.time - transportTime;
              const matchesName = targetMarker ? m.name === targetMarker : true;
              return timeToEvent > 0 && timeToEvent <= lookaheadSec && matchesName;
            });

            if (upcomingMarker) {
              const markerKey = `marker-${upcomingMarker.id}`;
              if (lastFired !== markerKey) {
                triggered = true;
                // We'll store the object ID in lastFired to avoid repeat triggers for the same event
                (rule as any)._lookaheadId = markerKey;
              }
            }
          } else if (eventType === 'CLIP_START' || eventType === 'CLIP_END') {
            const upcomingClip = clips.find(c => {
              const eventTime = eventType === 'CLIP_START' ? c.start : (c.start + c.duration);
              const timeToEvent = eventTime - transportTime;
              return timeToEvent > 0 && timeToEvent <= lookaheadSec;
            });

            if (upcomingClip) {
              const clipKey = `clip-${eventType}-${upcomingClip.id}`;
              if (lastFired !== clipKey) {
                triggered = true;
                (rule as any)._lookaheadId = clipKey;
              }
            }
          }
          break;
        }
        default:
          break;
      }
      return triggered;
    };

    rules.forEach((rule) => {
      if (!rule.enabled) return;

      const { trigger } = rule;
      let triggered = evaluateTrigger(trigger, rule);

      // Phase 5/6: Multi-Trigger Logic (AND/OR)
      if (trigger.params.secondaryTrigger && trigger.params.logicType) {
        const secondaryTriggered = evaluateTrigger(trigger.params.secondaryTrigger, rule);

        if (trigger.params.logicType === 'AND') {
          triggered = triggered && secondaryTriggered;
        } else if (trigger.params.logicType === 'OR') {
          triggered = triggered || secondaryTriggered;
        }
      }

      if (triggered) {
        // Phase 9: Dynamic Expression Evaluation for Registers
        const evaluateExpression = (expr: string, contextVal: any): any => {
          try {
            // Simple expression evaluator (e.g. "x * 0.5")
            // In a production app, we'd use a safer sandboxed parser
            const fn = new Function('x', 'registers', 'metrics', `return ${expr}`);
            return fn(contextVal, registers, { 
              lufs: loudnessMetrics?.integratedLUFS ?? 0,
              presence: masterAnalysis?.soul ?? 0
            });
          } catch (e) {
            console.error('[Nexus] Expression eval failed:', expr, e);
            return contextVal;
          }
        };

        // Handle Phase 7/9 Actions locally
        const performAction = (action: NexusAction) => {
          const { registerKey, registerValue, expression } = action.params;

          if (registerKey) {
            let finalVal = registerValue;
            if (expression) {
              const currentVal = registers[registerKey] ?? 0;
              finalVal = evaluateExpression(expression, currentVal);
            }
            setRegister(registerKey, finalVal);
          }

          // Phase 9: Engine Steering (Quantum Core / Five Pillars)
          if (action.type === 'QUANTUM_STEER' && action.params.engine) {
            console.log(`[Nexus] Steering ${action.params.engine}:${action.params.parameter} via ${action.params.expression || action.params.value}`);
            window.dispatchEvent(new CustomEvent('nexus:engine:steer', {
              detail: {
                engine: action.params.engine,
                parameter: action.params.parameter,
                value: action.params.value,
                expression: action.params.expression
              }
            }));
          }

          onAction(action);
        };

        if (rule.action.params.delayMs) {
          setTimeout(() => performAction(rule.action), rule.action.params.delayMs);
        } else {
          performAction(rule.action);
        }

        // lastFired timestamp: use beat for BEAT triggers, lookaheadId for lookahead, wall clock otherwise
        let timestamp: any = Date.now();
        if (trigger.type === 'BEAT') {
          timestamp = trigger.params.beat ?? currentBeat;
        } else if (trigger.type === 'ARRANGEMENT_LOOKAHEAD') {
          timestamp = (rule as any)._lookaheadId || Date.now();
        }

        setRules((prev) =>
          prev.map((r) => (r.id === rule.id ? { ...r, lastFired: timestamp } : r))
        );
      }
    });

    lastEvaluatedTimeRef.current = transportTime;
  }, [transportTime, isPlaying, rules, onAction, masterAnalysis, loudnessMetrics, bpm, registers]);

  return (
    <NexusLogicContext.Provider value={{ 
      rules, 
      addRule, 
      removeRule, 
      toggleRule,
      rejectRule,
      registers,
      setRegister,
      registerIntent,
      markers,
      clips,
      masterAnalysis,
      loudnessMetrics,
      trends,
      transportTime,
      bpm
    } as any}>
      {children}
    </NexusLogicContext.Provider>
  );
};

